import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './index.css';
import { request, response } from "express";


export default function Veiculos() {
    const [marca, setMarca] = useState('');
    const [modelo, setModelo] = useState('');
    const [cadastrar, setCadastrar] = useState('');
    const [carros, setCarros] = useState([]);
    const [post, setDataPost] = useState({});

    function resetForm() {
        setMarca('');
        setModelo('');
        setCadastrar('');
    }

    const getApiData = async () => {
        const request = await fetch("http://127.0.0.1:5000/api/tasks", {
            mode: "no-cors",
        }).then((request) => response.json());



        setCarros(response)
    }

    useEffect(() => {
        getApiData();
    }, []);




    async function enviarCadastro(event) {

        event.preventDefault() // previne o comportamento padrão do form de recarregar a página

        const requestConfig = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ nome: marca, modelo, cadastrar })
        }

        await fetch(request, requestConfig)
            .then(resp => resp.json())
            .then(data => {
                setDataPost(data)
                resetForm();
            }).catch(error => {
                console.log(error);
            })
    }

    return (
        <>
            <header>
                <p>
                    {/* <Link to="/home">Home</Link> */}
                </p>
            </header>
            <form name="cadastroForm" onSubmit={enviarCadastro}>
                <h1>Cadastrar Veiculo</h1>
                <label htmlFor="marca">Marca</label>
                <input
                    type="text"
                    id="marcaid"
                    placeholder="FIAT"
                    required="required"
                    name="Marca"
                    value={marca}
                    onChange={e => setMarca(e.target.value)}

                /><br></br>
                <label htmlFor="fone" id="foneIdLabel">Tel</label>
                <input
                    type="modelo"
                    id="foneid"
                    placeholder="Argo"
                    name="Modelo"
                    value={modelo}
                    onChange={e => setModelo(e.target.value)}
                /><br></br>
                <textarea placeholder="Cadastrar Aqui"
                    value={cadastrar}
                    onChange={e => setCadastrar(e.target.value)}
                ></textarea>
                <input
                    type="submit"
                    className="enviar"
                /><br></br>
            </form>
        </>
    );
};
